package landOfJava;

public class LearnLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i = 1; i<=10; i++) {
	System.out.println(i);
	
}
System.out.println("increased by 1");

for (int i = 10;i>=1; i--) {
	System.out.println(i);
}
	System.out.println("decreased by 1");
	System.out.println("Exit");
	
      }
	}


